// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "../node_modules/@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "../node_modules/@openzeppelin/contracts/utils/Counters.sol";
import "../node_modules/@openzeppelin/contracts/utils/introspection/ERC165.sol";

interface IERC2981Royalties {
    function royaltyInfo(
        uint256 _tokenId,
        uint256 _value
    ) external view returns (address _receiver, uint256 _royaltyAmount);
}

contract Royalties is IERC2981Royalties, ERC165 {
    struct RoyaltyInfo {
        address recipient;
        uint24 amount;
    }

    mapping(uint256 => RoyaltyInfo) internal _royalties;

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override returns (bool) {
        return
            interfaceId == type(IERC2981Royalties).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function _setTokenRoyalty(
        uint256 tokenId,
        address recipient,
        uint256 value
    ) internal {
        require(value <= 10000, "ERC2981Royalties: Too high");
        _royalties[tokenId] = RoyaltyInfo(recipient, uint24(value));
    }

    function royaltyInfo(
        uint256 tokenId,
        uint256 value
    ) external view override returns (address receiver, uint256 royaltyAmount) {
        RoyaltyInfo memory royalties = _royalties[tokenId];
        receiver = royalties.recipient;
        royaltyAmount = (value * royalties.amount) / 10000;
    }
}

contract WeaponSFT is ERC1155, Royalties {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;

    struct Weapon {
        string name;
        uint boostXP;
        uint boostGold;
    }

    Weapon[] weapons;

    constructor()
        ERC1155(
            "https://gateway.pinata.cloud/ipfs/QmaYXNwqJSbJ8de65ncqo2z8hZBJA7XPZiS4bnscNdD1EP/{id}.json"
        )
    {}

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(ERC1155, Royalties) returns (bool) {
        return super.supportsInterface(interfaceId);
    }

    function MintWeapons(
        address player,
        string memory name,
        uint boostXP,
        uint boostGold,
        uint number
    ) public returns (uint) {
        _tokenIds.increment();
        weapons.push(Weapon(name, boostGold, boostXP));
        uint256 newItemId = _tokenIds.current();
        _mint(player, newItemId, number, "");
        _setTokenRoyalty(newItemId, msg.sender, 1000);

        return newItemId;
    }

    function init() public {
        MintWeapons(msg.sender, "Classic sword", 0, 0, 2 * 10 ** 2);
        MintWeapons(msg.sender, "Unique sword", 30, 30, 1);
    }
}
